/*
  function draw() external returns (bytes32);
  function enter() external payable returns (bytes32);
  function startNewRound() external returns (bool);
*/
import { ethers } from "ethers";

import { Container, Row, Col } from 'react-bootstrap';
import FancyButton from './FancyButton';

import './Dashboard.css';

function constructor(props) {

    // We store multiple things in Dapp's state.
    // You don't need to follow this pattern, but it's an useful example.
    this.initialState = {
      // The info of the token (i.e. It's Name and symbol)
      tokenData: undefined,
      // The user's address and balance
      selectedAddress: undefined,
      balance: undefined,
      // The ID about transactions being sent, and any possible error with them
      txBeingSent: undefined,
      transactionError: undefined,
      networkError: undefined,
    };

    this.state = this.initialState;
  }

function ethersStuff() {

}


function Dashboard() {
  if (window.ethereum === undefined) {
      return <div>No wallet.</div>;
  }
  return (
    <Container>
      <div className="dash-container">
        <Row className="justify-content-center">
          <p>FLotto</p>
        </Row>
        <Row>
          <Col>
            {/* Will display ticket number when they enter */}
            <FancyButton text={"Enter"}/>
          </Col>
          <Col>
            {/* Display winning all addresses */}
            <FancyButton text={"Draw"}/>
            {/*<div><p>Text</p></div>*/}
          </Col>
          <Col>
            {/* Disable until winner is drawn */}
            <FancyButton text={"Get Paid"} disabled={true}/>
          </Col>
        </Row>
      </div>

      <Row>
        <Col className="pt-5">
          <FancyButton text={"Connect Wallet"}/>
        </Col>
      </Row>
     </Container>
  );
}

export default Dashboard;
